class ProductDetails extends Component {
  constructor(props) {
    super(props);

    this.state = {
      location: '',
      productId: '',
    }
  }

  componentDidMount() {


 const { productId } = this.props;
    this.setState({
      productId,
    }); 


  }


  render() {
    const { productId } = this.props;
    return (
      <div>
        {productId}
