import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchGetProductsFromCategoryAndQuery, searchInputValueAction } from '../actions';
import { getProductsFromCategoryAndQuery } from '../services/api';

class SearchInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchInputValue: '',
    };
  }

  componentDidMount() {

  }

  onChangeSearchInputValue = (event) => {
    this.setState({ searchInputValue: event.target.value })
  }

  onKeyPressSearch = (event) => {
    const { getProductsFromCategoryAndQuery, searchInputValueAction } = this.props;
    const value = event.target.value.replace(/\s/g, '');
    const { searchInputValue } = this.state;
    let message;
    if (event.keyCode === 13 && value) {
      getProductsFromCategoryAndQuery(value);
      message = '';
      searchInputValueAction(searchInputValue, message)
      this.setState({ searchInputValue: '' });
    } else if (event.keyCode === 13 && !value) {
      message = 'Digite algum termo para ser buscado';
      searchInputValueAction(value, message);
    }
  }

  onClickSearch = (value) => {
    const { getProductsFromCategoryAndQuery, searchInputValueAction } = this.props;
    const { searchInputValue } = this.state;
    let message;
    if (value.replace(/\s/g, '')) {
      getProductsFromCategoryAndQuery(value.replace(/\s/g, ''));
      message = '';
      searchInputValueAction(searchInputValue, message)
      this.setState({ searchInputValue: '' });
    } else {
      message = 'Digite algum termo para ser buscado';
      searchInputValueAction(value.replace(/\s/g, ''), message)
    }
  }

  render() {
    const { searchInputValue } = this.state;
    return (
      <div>
        <input
          type="text"
          placeholder="Pesquisar"
          value={searchInputValue}
          onChange={(e) => this.onChangeSearchInputValue(e)}
          onKeyUp={(e) => this.onKeyPressSearch(e)}
        />
        <button onClick={() => this.onClickSearch(searchInputValue)}>Lupa</button>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
  getProductsFromCategoryAndQuery: (value) => (dispatch(fetchGetProductsFromCategoryAndQuery(null, value))),
  searchInputValueAction: (value, message) => (dispatch(searchInputValueAction(value, message)))
});

export default connect(null, mapDispatchToProps)(SearchInput);
