import { combineReducers } from 'redux';
import {
  REQUEST_DATA,
  RECEIVE_DATA,
  RECEIVE_FAIL,
} from '../actions';

const stateDefault = {
  isFetching: false,
  data: [],
  resServer: false,
};

const requestData = (state = stateDefault, action) => {
  switch (action.type) {
    case REQUEST_DATA:
      return {
        ...state,
        isFetching: true,
      };
    case RECEIVE_DATA:
      return {
        ...state,
        isFetching: false,
        data: action.data,
        resServer: true,
      };
    case RECEIVE_FAIL:
      return {
        ...state,
        error: action.error,
      };
    default:
      return state;
  }
};

const rootReducer = combineReducers({ requestData });

export default rootReducer;
