import React, { Component } from 'react';
import { getCategories } from '../services/api'
import Loading from './Loading';
import { connect } from 'react-redux';

class AsideCategories extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      categories: [],
    };
  }

  componentDidMount() {
    getCategories()
      .then((categories) => {
        console.log(categories)
        this.setState({
          categories,
          isLoading: false,
        })
      })
  }

  render() {

    const { isLoading, categories } = this.state;

    if (isLoading) return <Loading />;

    return (
      <div>
        {categories.map((categorie) =>
          <div key={categorie.id}>
            <input type="radio" id={categorie.id} name="categorieItem" value={categorie.id} onChange={(e) => console.log(e.target.value)}/>
            <label htmlFor={categorie.id}>{categorie.name}</label>
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  searchInputValueReducer: state.searchInputValueReducer,
});

const mapDispatchToProps = (dispatch) => ({
  fetchGetProductsFromCategoryAndQuery: (categoryId, query) => (dispatch(fetchGetProductsFromCategoryAndQuery(categoryId, query)))
})

export default connect(mapStateToProps)(AsideCategories);
