import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getCategories } from '../services/api'
import Loading from './Loading';
import {
  fetchGetProductsFromCategoryAndQuery,
  categoryIdValueAction,
  cancelRequest,
  searchInputValueAction,
} from '../actions';


class AsideCategories extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      categories: [],
    };
  }

  componentDidMount() {
    getCategories()
      .then((categories) => {
        this.setState({
          categories,
          isLoading: false,
        })
      })
  }

  onChangeSearchCategoryId(event) {
    const { searchInputValueReducer } = this.props;
    const {
      fetchGetProductsFromCategoryAndQuery,
      categoryIdValueAction,
      searchInputValueAction,
      cancelRequest,
      data,
      resServer } = this.props;
    const categoryId = event.target.value;
    const query = searchInputValueReducer.replace(/\s/g, '');
    categoryIdValueAction(categoryId);
    if (!categoryId && !query) {
      cancelRequest();
    }
    if (resServer && data.results.length === 0) {
      fetchGetProductsFromCategoryAndQuery(categoryId, '');
      /* searchInputValueAction('', ''); */
      console.log(categoryId)
    }
    if (categoryId || query) {
      fetchGetProductsFromCategoryAndQuery(categoryId, query);
    }
  }

  render() {

    const { isLoading, categories } = this.state;

    if (isLoading) return <Loading />;

    return (
      <div>
        <input
          type="radio"
          id="input-empty"
          name="categorieItem"
          value=""
          onChange={(e) =>
            this.onChangeSearchCategoryId(e)} />
        <label htmlFor="input-empty">Sem categoria</label>
        {categories.map((categorie) =>
          <div key={categorie.id}>
            <input
              type="radio"
              id={categorie.id}
              name="categorieItem"
              value={categorie.id}
              onChange={(e) =>
                this.onChangeSearchCategoryId(e)} />
            <label htmlFor={categorie.id}>{categorie.name}</label>
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  searchInputValueReducer: state.searchInputValueReducer.value,
  categoryIdValueReducer: state.categoryIdValueReducer.value,
  data: state.requestData.data,
  resServer: state.requestData.resServer,
});

const mapDispatchToProps = (dispatch) => ({
  fetchGetProductsFromCategoryAndQuery: (categoryId, query) => (dispatch(fetchGetProductsFromCategoryAndQuery(categoryId, query))),
  categoryIdValueAction: (value) => (dispatch(categoryIdValueAction(value))),
  searchInputValueAction: (value, message) => (dispatch(searchInputValueAction(value, message))),
  cancelRequest: () => (dispatch(cancelRequest())),
});

export default connect(mapStateToProps, mapDispatchToProps)(AsideCategories);
