import React, { Component } from 'react';
import { getProductsFromCategoryAndQuery } from '../services/api';

class SearchInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchInputValue: '',
    };
  }

  onChangeSearchInputValue = (event) => {
    this.setState({ searchInputValue: event.target.value })
  }

  onKeyPressSearch = (event) => {
    console.log('teste', event.keyCode, event.target.value)
    if (event.keyCode === 13) {
      getProductsFromCategoryAndQuery(null, event.target.value)
        .then((res) => console.log(res.results));
    }
  }

  onClickSearch = (value) => {
    getProductsFromCategoryAndQuery(null, value)
      .then((res) => console.log(res.results));
  }

  render() {
    const { searchInputValue } = this.state;
    return (
      <div>
        <input
          type="text"
          placeholder="Pesquisar"
          onChange={(e) => this.onChangeSearchInputValue(e)}
          onKeyUp={(e) => this.onKeyPressSearch(e)}
        />
        <button onClick={() => this.onClickSearch(searchInputValue)}>Lupa</button>
      </div>
    )
  }
}

export default SearchInput;
