import { combineReducers } from 'redux';
import { SEARCH_QUERY } from '../actions';

const stateDefault = {
  isFetching: false,
  data: [],
};

const requestData = (state = stateDefault, action) => {
  switch (action.type) {
    case REQUEST_DATA:
      return {
        ...state,
        isFetching: true,
      };
    case RECEIVE_DATA:
      return {
        ...state,
        isFetching: false,
        data: action.data,
      };
    case RECEIVE_FAIL:
      return {
        ...state,
        error: action.error,
      };
    default:
      return state;
  }
};

const rootReducer = combineReducers({ requestData });

export default rootReducer;
