import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchGetProductsFromCategoryAndQuery } from '../actions';
import { getProductsFromCategoryAndQuery } from '../services/api';

class SearchInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchInputValue: ' ',
    };
  }

  componentDidMount() {

  }

  onChangeSearchInputValue = (event) => {
    this.setState({ searchInputValue: event.target.value })
  }

  onKeyPressSearch = (event) => {
    if (event.keyCode === 13 && event.target.value) {
      const { getProductsFromCategoryAndQuery } = this.props;
      getProductsFromCategoryAndQuery(event.target.value);
      this.setState({ searchInputValue: ' ' });
    }
  }

  onClickSearch = (value) => {
    if (value) {
      const { getProductsFromCategoryAndQuery } = this.props;
      getProductsFromCategoryAndQuery(value);
      this.setState({ searchInputValue: ' ' });
    }
  }

  render() {
    const { searchInputValue } = this.state;
    return (
      <div>
        <input
          type="text"
          placeholder="Pesquisar"
          value={searchInputValue}
          onChange={(e) => this.onChangeSearchInputValue(e)}
          onKeyUp={(e) => this.onKeyPressSearch(e)}
        />
        <button onClick={() => this.onClickSearch(searchInputValue)}>Lupa</button>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => ({
  getProductsFromCategoryAndQuery: (value) => (dispatch(fetchGetProductsFromCategoryAndQuery(null, value))),
});

export default connect(null, mapDispatchToProps)(SearchInput);
