import React, { Component } from 'react';
import { connect } from 'react-redux';


class ListProducts extends Component {
  constructor(props) {
    super(props);

    this.state = {

    };

  }

  consoleTeste() {
    const { data } = this.props;
    const { requestData } = this.props;
    console.log(requestData)
    console.log(data)
  }

  render() {

    const { isFetching } = this.props;
    const { resServer } = this.props;
    const { data } = this.props;
    const { value, message } = this.props.searchInputValueReducer;

    if (message) return <p>{message}</p>

    if (!resServer) return <p>Você ainda não realizou uma busca</p>

    if (resServer && data.length === 0) return <p>Nenhum resultado encontrado para o termo "{value}"</p>

    if (isFetching) return <p>Carregando...</p>

    return (
      <div>
        <div>
          <ul>
            {data.map((product) =>
              <li key={product.id}>
                <img src={product.thumbnail} alt={product.title} />
                <div className="infos-product">
                  <h2>{product.title}</h2>
                  <p>
                    {
                      Number.isInteger(product.price) ?
                        product.price :
                        product.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })
                    }
                  </p>
                  <p>
                    {product.installments.quantity}x
                    {
                      product.installments.amount.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })
                    }
                  </p>
                </div>
              </li>
            )}
          </ul>
        </div>
        <button onClick={() => this.consoleTeste()}>teste</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  requestData: state.requestData,
  isFetching: state.requestData.isFetching,
  data: state.requestData.data,
  resServer: state.requestData.resServer,
  searchInputValueReducer: state.searchInputValueReducer,
})


export default connect(mapStateToProps)(ListProducts);
