import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getCategories } from '../services/api'
import Loading from './Loading';
import { fetchGetProductsFromCategoryAndQuery } from '../actions';
import { getProductsFromCategoryAndQuery } from '../services/api';


class AsideCategories extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      categories: [],
    };
  }

  componentDidMount() {
    getCategories()
      .then((categories) => {
        this.setState({
          categories,
          isLoading: false,
        })
      })
  }

  onChangeSearchCategoryId(event) {
    const { searchInputValueReducer } = this.props;
    const { fetchGetProductsFromCategoryAndQuery } = this.props;
    const categoryId = event.target.value;
    const query = searchInputValueReducer.replace(/\s/g, '');
    if (categoryId) {
      fetchGetProductsFromCategoryAndQuery(categoryId, query);
      getProductsFromCategoryAndQuery(categoryId, query)
        .then((res) => console.log(res))
    }
  }

  render() {

    const { isLoading, categories } = this.state;

    if (isLoading) return <Loading />;

    return (
      <div>
        {categories.map((categorie) =>
          <div key={categorie.id}>
            <input type="radio" id={categorie.id} name="categorieItem" value={categorie.id} onChange={(e) => this.onChangeSearchCategoryId(e)} />
            <label htmlFor={categorie.id}>{categorie.name}</label>
          </div>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  searchInputValueReducer: state.searchInputValueReducer,
});

const mapDispatchToProps = (dispatch) => ({
  fetchGetProductsFromCategoryAndQuery: (categoryId, query) => (dispatch(fetchGetProductsFromCategoryAndQuery(categoryId, query)))
})

export default connect(mapStateToProps, mapDispatchToProps)(AsideCategories);
