import { combineReducers } from 'redux';
import { SEARCH_QUERY } from '../actions';

const searchStateDefault = {
  requestData: false,
  value: '',
};

function searchInputValueReducer(state = searchStateDefault, action) {
  switch (action.type) {
    case SEARCH_QUERY:
      return {
        ...state,
        requestData: true,
        value: action.value,
      }
    default:
      return state;
  }
}

const rootReducer = combineReducers({ searchInputValueReducer });

export default rootReducer;
