import React, { Component } from 'react';
import { getProductsFromCategoryAndQuery } from '../services/api';

class SearchInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchInputValue: '',
    };
  }

  onChangeSearchInputValue = (value) => {
    this.setState({ searchInputValue: value })
  }

  onKeyPressSearch = (keyCode, value) => {
    if (keyCode === 13) {
      getProductsFromCategoryAndQuery(null, value)
        .then((res) => console.log(res.results));
    }
  }

  onClickSearch = (value) => {
    getProductsFromCategoryAndQuery(null, value)
      .then((res) => console.log(res.results));
  }

  render() {
    const { searchInputValue } = this.state;
    return (
      <div>
        <input
          type="text"
          placeholder="Pesquisar"
          onChange={(e) => this.onChangeSearchInputValue(e.target.value)}
          onKeyPress={(e) => this.onKeyPressSearch(e.keyCode, e.target.value)}
        />
        <button onClick={() => this.onClickSearch(searchInputValue)}>Lupa</button>
      </div>
    )
  }
}

export default SearchInput;
