import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchGetProductsFromCategoryAndQuery } from '../actions';

class SearchInput extends Component {
  constructor(props) {
    super(props);

    this.state = {
      searchInputValue: '',
    };
  }

  onChangeSearchInputValue = (event) => {
    this.setState({ searchInputValue: event.target.value })
  }

  onKeyPressSearch = (event) => {
    const { fetchGetProductsFromCategoryAndQuery, searchInputValueAction, categoryIdValueReducer } = this.props;
    const value = event.target.value.replace(/\s/g, '');
    const { searchInputValue } = this.state;
    let message;
    if (event.keyCode === 13 && value) {
      fetchGetProductsFromCategoryAndQuery(categoryIdValueReducer, value);
      message = '';
      searchInputValueAction(searchInputValue, message)
      this.setState({ searchInputValue: '' });
    } else if (event.keyCode === 13 && !value) {
      message = 'Você deve digitar algo para buscar ou selecionar uma categoria';
      searchInputValueAction(value, message);
    }
  }

  onClickSearch = (value) => {
    const { fetchGetProductsFromCategoryAndQuery, searchInputValueAction, categoryIdValueReducer } = this.props;
    const { searchInputValue } = this.state;
    let message;
    if (value.replace(/\s/g, '')) {
      fetchGetProductsFromCategoryAndQuery(categoryIdValueReducer, value.replace(/\s/g, ''));
      message = '';
      searchInputValueAction(searchInputValue, message)
      this.setState({ searchInputValue: '' });
    } else {
      message = 'Você deve digitar algo para buscar ou selecionar uma categoria';
      searchInputValueAction(value.replace(/\s/g, ''), message)
    }
  }

  render() {
    const { searchInputValue } = this.state;
    return (
      <div>
        <input
          type="text"
          placeholder="Pesquisar"
          value={searchInputValue}
          onChange={(e) => this.onChangeSearchInputValue(e)}
          onKeyUp={(e) => this.onKeyPressSearch(e)}
        />
        <button onClick={() => this.onClickSearch(searchInputValue)}>Lupa</button>
      </div>
    )
  }
}

const mapStateToProps = (state) => ({
  categoryIdValueReducer: state.categoryIdValueReducer.value,
});

const mapDispatchToProps = (dispatch) => ({
  fetchGetProductsFromCategoryAndQuery: (categoryId, query) => (dispatch(fetchGetProductsFromCategoryAndQuery(categoryId, query))),

});

export default connect(mapStateToProps, mapDispatchToProps)(SearchInput);
