import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import shoppingCartImg from '../images/shopping-cart.png';

class Header extends Component {
  constructor(props) {
    super(props);
  }


  componentDidMount() {
    const localStorageShoppingCart = JSON.parse(localStorage.getItem('cart'));
    console.log(localStorageShoppingCart)
    const sum = localStorageShoppingCart.reduce((ac, cc) => ac.amount + cc.amount)
    console.log(sum)
  }

  render() {

    const { totalAmountProducts } = this.props;

    return (
      <header>
        <Link to="shopping-cart">
          <img src={shoppingCartImg} alt="Carrinho de compras" style={{ width: '50px' }} />
          <p>{totalAmountProducts}</p>
        </Link>
      </header>
    );
  }
}

const mapStateToProps = (state) => ({
  totalAmountProducts: state.shoppingCart.totalAmount,
});

export default connect(mapStateToProps)(Header);
